import React ,{useState,useEffect} from 'react'
import './post.css'
import MoreVertIcon from '@mui/icons-material/MoreVert';

import axios from "axios"
function Post({post}) {
   const [like,setLike]=useState(post.like)
   const [isLiked,setIsliked]=useState(false) 
   const [user,setUser]=useState({})

   const PF = process.env.REACT_APP_PUBLIC_FOLDER;
   const clickHandle=()=>{
    setLike(isLiked ? like-1 : like+1)
    setIsliked(!isLiked)
   }
   useEffect(() => {
    const fetchPosts=async ()=>{
     const res=await axios.get("posts/timeline/6326ccc57e18a32dc0dec9de")
     setUser(res.data)
     console.log(res.data)
    }
    fetchPosts()
  },[])
  return (
    <div className='post'>
    <div className="postWrapper">
        <div className='postTop'>
            <div className='postTopLeft'>
                <img className='profilepicture' src={Users.filter((u) => u.id === post?.userId)[0].profilePicture} alt=""/>
                <span className='profilename'>{Users.filter((u) => u.id === post?.userId)[0].username}</span>
                <span className='profiletime'>{post.date}</span>
            </div>
            <div className='postTopRight'>
                <MoreVertIcon/>
            </div>
        </div>
        <div className='postCentre'>
            <span className='postdesc'>{post?.desc}</span>
            <img className='postPic' src={PF+post.photo} alt=""/>

        </div>
        <div className='postBottom'>
        <div className='postBottomLeft'>
        <img className='loveButton' src={`${PF}posts/23.png`} onClick={clickHandle}   alt=""/>
        <img className='likeButton' src={`${PF}posts/24.png`} onClick={clickHandle} alt=""/>
        <span className='postlikescount'>{like} likes</span>
        </div>
        <div className='postBottomRight'>
        <span className='postcomment'>{ post.comment} comments</span>
        </div>
         </div>
    </div>
    </div>
  )
}

export default Post
