import React,{useRef} from 'react'
import './share.css'
import PermMediaIcon from '@mui/icons-material/PermMedia';
import LabelIcon from '@mui/icons-material/Label';
import RoomIcon from '@mui/icons-material/Room';
import EmojiEmotionsIcon from '@mui/icons-material/EmojiEmotions';
import axios from "axios";
import { useContext, useState } from "react";
import {AuthContext} from "../../context/AuthContext.jsx"

function Share() {
    const { user } = useContext(AuthContext);
    const desc=useRef()
    const PF=process.env.REACT_APP_PUBLIC_FOLDER;
    const [file, setFile] = useState(null);
    const submithandler = async (e) =>{
         e.preventDefault()
         const newPost = {
            userId : user._id,
            desc:desc.current.value
         };
         if(file){
            const data= new FormData();
            const filename = new Date()
         }
         try{
          await axios.post("/posts",newPost)
         }catch(e){
         
         }

    }
  return (
    <div className='share'>
        <div className='shareWrapper'>
        <div className="shareTop">
              <img className="shareProfileimage"  
              src={
              user.profilePicture
                ? PF + user.profilePicture
                : PF + "person/avatar.jpg"
            } alt=""/>
              <input className='shareInput' placeholder={"what's in your mind " +  user.username +"?"}/>
        </div>
        <hr className='sharehr'/>
        <div className='shareBottom'>
            <form className='shareOptions' onSubmit={submithandler}>
                <label htmlFor="file" className='shareOption'>
                     <PermMediaIcon htmlColor='tomato' className='shareIcon'/>
                     <span className="shareIconText">Video or Photo</span>
                     <input style={{display:"none"}} type="file" id="file" accept=".png,.jpeg,.jpg" onChange={(e)=>setFile(e.target.files[0])} />
                </label>
                <div className='shareOption'>
                    <LabelIcon htmlColor='blue' className="shareIcon"/>
                    <span className='shareIconText'>Tag</span>
                </div>
                <div className='shareOption'>
                    <RoomIcon htmlColor="violet" className="shareIcon"/>
                    <span className='shareIconText'>Location</span>
                </div>
                <div className='shareOption'>
                    <EmojiEmotionsIcon htmlColor='yellow' className="shareIcon"/>
                    <span className='shareIconText'>Emotion</span>
                </div>
                <button className="shareButton" type="submit">Share</button>
            </form>
           
        </div>
        </div>
        </div>
    
  )
}

export default Share
