import { createContext,useReducer } from "react"
import AuthReducer from "./AuthReducer"
const initialState={
    user:{
        _id: "6326ccc57e18a32dc0dec9de",
        username: "Selena Gomez",
        email: "selina@gmail.com",
        profilePicture: "",
        "coverPicture": "",
        "followers": [],
        "followings": [],
        "isAdmin": false,
        "desc": "Welcome to my page",
        "city": "Sydney",
        "from": "Austrelia",
        "relationship": 1,
        "createdAt": "2022-09-18T07:46:13.932Z",
        "updatedAt": "2022-09-18T07:46:13.932Z",
        "__v": 0
    },
    isFetching:false,
    error:false
}

export const AuthContext = createContext(initialState)

export const ContextProvider = ({children})=>{
     const [state, dispatch] = useReducer(AuthReducer, initialState)

     return (
        <AuthContext.Provider
        value={{
            user:state.user,
            isFetching:state.isFetching,
            error:state.error,
            dispatch
        }}>
        {children}
        </AuthContext.Provider>
     )
}
